import React from "react";
import PropTypes from "prop-types";

// Function to highlight specific text in the paragraph
const highlightInParagraph = (text, term) => {
    if (!text || !term) return text;
    const regex = new RegExp(`(${term})`, 'gi');
    return text.replace(regex, '<span class="highlight">$1</span>');
};

const Header = (props) => {
    const title = props.data ? props.data.title : "Loading";
    const paragraph = props.data ? props.data.paragraph : "Loading";

    return ( <
        header id = "header" >
        <
        div className = "intro" >
        <
        div className = "overlay" >
        <
        div className = "container" >
        <
        div className = "row" >
        <
        div className = "col-md-8 col-md-offset-2 intro-text" >
        <
        h1 > { title } < /h1> <
        p dangerouslySetInnerHTML = {
            { __html: highlightInParagraph(paragraph, "Arcnet") } }
        /> {
            /* <a
                              href="#features"
                              className="btn btn-custom btn-lg page-scroll"
                            >
                              Learn More
                            </a>{" "} */
        } <
        /div> <
        /div> <
        /div> <
        /div> <
        /div> <
        /header>
    );
};

// Define PropTypes for the component
Header.propTypes = {
    data: PropTypes.shape({
        title: PropTypes.string,
        paragraph: PropTypes.string
    })
};
